#include <iostream>
#include <string>

using namespace std;

/*
Given two strings s and t, return true if s is a subsequence of t, or false otherwise.

A subsequence of a string is a new string that is formed from the original string by deleting some (can be none) of the characters
 without disturbing the relative positions of the remaining characters. (i.e., "ace" is a subsequence of "abcde" while "aec" is not).

EX
	Input: s = "abc", t = "ahbgdc"
	Output: true

	Input: s = "axc", t = "ahbgdc"
	Output: false

Constraints
	0 <= s.length <= 100
	0 <= t.length <= 104
	s and t consist only of lowercase English letters.

Input
	"acb"
	"ahbgdc"
Expec
	false

Input
	"ab"
	"baab"
Expec
	true

Input
	"leeeeetcode"
	""
*/


class IsSubsequence
{
public:
	bool getIsSubsequence(string s, string t) {
		/*
		*	// 중복 제거를 위한 정렬
			sort(t.begin(), t.end());
			sort(s.begin(), s.end());
			// 중복 제거 후 빈메모리제거
			t.erase(unique(t.begin(), t.end()),t.end());
			s.erase(unique(s.begin(), s.end()),s.end());
		*/

		//for (int i = t.size(); i > 0; i--)
		//{
		//	if (s.find(t[i]) == string::npos)		// find 해당 char 가 없으면 쓰레기값(string::npos) 리턴
		//	{
		//		t.erase(t.begin() + i);
		//	}
		//}

		cout << "s :: " << s << endl;
		cout << "t :: " << t << endl;

		int cnt = 0, slen = s.size(), tlen = t.size();
		int j = 0;
		for (int i = 0; i < slen; i++)
		{
			for (; j < tlen; j++)
			{
				if (s[i] == t[j])
				{
					j++;
					cnt++;
					cout << "i :: " << i << ", j :: " << j << " s :: " << s[i] << " t :: " << t[j] << " findx :: " << j << endl;
					break;

				}
			}
		}

		cout << "cnt :: " << cnt << endl;

		if (slen == cnt)
		{
			return true;
		}

		return false;
	}

	bool getIsSubsequence2(string s, string t) {

		int j = 0;

		for (int i = 0; i < t.length() && j < s.length(); i++)
		{
			if (s[j] == t[i])
			{
				j++;
			}
		}

		return (j == s.size());
	}
};